function ArticleCard(props) {
   const {article} = props;

const handleClick = (event) => {

	<Link to={`/${article.article_id}`}/>
}



    return (

     	<li className="article-card">
		<h4>{article.title}</h4>

		<p>{article.topic}</p>
		<button onClick={handleClick}>Read </button>
		</li>


	)

}

export default ArticleCard;
