import {useState, useEffect} from "react";
import { useParams } from "react-router-dom";
import { fetchCommentsById } from "../utils/API";
import CommentCard from "./Comment-Card";


function CommentList(props) {

    const { article_id } = useParams();
    console.log(article_id,"articleid!!!!!!")
    const [comments, setComments] = useState([])
    const [commentsIsLoading, setCommentsIsLoading] = useState(true)



        useEffect(() => {
            setCommentsIsLoading(true);
            fetchCommentsById(article_id).then((commentData)=> {
                console.log(commentData, "<-----")
              setComments(commentData)
              setCommentsIsLoading(false)
            })
          }, []) 



        return (
        <section className="comment-list">
            <h4>Comments</h4>
           {
           commentsIsLoading ? (<p>Comments are loading...</p>) :  
           (<ul>
            {comments.map((comment) => {
              return (
               <CommentCard 
               key = {comment.comment_id}
               comment = {comment}
               />
              );
                  })}
          </ul>) 
          }

        </section>
        )
    }

    export default CommentList;
